        transactionid = UUID.randomUUID().toString()
        flowFile = session.putAttribute(flowFile, 'transactionId', transactionid)
    }

    // Read original JSON string
    def originalJsonText = ''
    session.read(flowFile, { inputStream ->
        originalJsonText = inputStream.text
    } as InputStreamCallback)

    def json = new JsonSlurper().parseText(originalJsonText)

    // Extract objectid from JSON (Product_id: "1" in your example)
    def productId = json['Product_id'] ?: 0L // Fallback to 0 if missing

    // Prepare new JSON object matching schema column names (lowercase)
