// Matches DB schema lowercase field names.
// Incoming flow file content is the new JSON to set as targetjson (string for JSONB), but optional in error case.
// Assumes transactionid in flow file attribute 'transactionId' (camelCase).
// Does not update other fields like objectid, process_group (ignored if not present).
import groovy.json.JsonSlurper
import groovy.json.JsonOutput
import java.util.UUID

def flowFile = session.get()
if (!flowFile) return

try {
    // Get transactionid from attribute (for WHERE clause)
    def transactionid = flowFile.getAttribute('transactionId')
    if (!transactionid) {
        log.warn('No transactionId; generating new')
        transactionid = UUID.randomUUID().toString()
        flowFile = session.putAttribute(flowFile, 'transactionId', transactionid)
    }

    // Read incoming JSON (new content for targetjson) - optional, set to null if no content
    def incomingJsonText = null
    try {
        session.read(flowFile, { inputStream ->
            incomingJsonText = inputStream.text
        } as InputStreamCallback)
    } catch (Exception readEx) {
        log.warn('No incoming JSON for targetjson in error case; setting to null')
    }

    // Get invokehttp.status.code for error
    def statusCode = flowFile.getAttribute('invokehttp.status.code') ?: 'unknown'
    def statusCodeRecord = flowFile.getAttribute('invokehttp.response.body') ?: 'unknown'
    def status = (statusCode != '200') ? 'fail' : 'init'  // Set to 'fail' if not 200

    // Prepare minimal JSON for UPDATE: key + fields to set
    def newRecord = [
        transactionid: transactionid, // For WHERE; ignored in SET
        status: status,
        error: statusCodeRecord,  // Write status code to error column
        targetjson: incomingJsonText  // New JSON if available, else null
    ]

    def newJsonText = JsonOutput.toJson(newRecord)

    // Write to flow file
    flowFile = session.write(flowFile, { outputStream ->
        outputStream.write(newJsonText.getBytes('UTF-8'))
    } as OutputStreamCallback)

    flowFile = session.putAttribute(flowFile, 'mime.type', 'application/json')

    // Optional: Log for debug
    log.info("Prepared ERROR UPDATE JSON: transactionid=${transactionid}, status=${status}, error=${statusCode}")

    session.transfer(flowFile, REL_SUCCESS)

} catch (Exception e) {
    log.error('Error in ERROR UPDATE prep script', e)
    session.transfer(flowFile, REL_FAILURE)
}
