import groovy.json.JsonSlurper
import groovy.json.JsonOutput
import java.util.UUID

def flowFile = session.get()
if (!flowFile) return

try {
    // Get transactionid from attribute (UUID string), generate if missing
    def transactionid = flowFile.getAttribute('transactionId')
    if (!transactionid) {
        transactionid = UUID.randomUUID().toString()
        flowFile = session.putAttribute(flowFile, 'transactionId', transactionid)
    }

    // Read original JSON from flowfile content
    def originalJsonText = ''
    session.read(flowFile, { inputStream ->
        originalJsonText = inputStream.text
    } as InputStreamCallback)

    def json = new JsonSlurper().parseText(originalJsonText)

    // Extract objectid from JSON (customerId field as string for text type)
    def objectid = json['customerId'] ?: null
    if (!objectid) {
        log.error('Missing customerId in JSON')
        session.transfer(flowFile, REL_FAILURE)
        return
    }

    // Prepare new JSON record matching table schema
    def newRecord = [
        transactionid: transactionid,  // UUID string
        process_group: objectid,       // Same as objectid
        objectid: objectid,            // From JSON customerId (text)
        objectname: 'orders-playment', // Static value
        eventtype: 'orders',           // Static value
        status: 'init',                // Default status
        error: null,                   // Null as per schema
        sourcejson: originalJsonText,  // Original JSON body (with arrays and nulls)
        targetjson: null               // Null as per requirements
        // timestamp omitted - use DB default CURRENT_TIMESTAMP
    ]

    def newJsonText = JsonOutput.toJson(newRecord)

    // Write new JSON to flowfile content
    flowFile = session.write(flowFile, { outputStream ->
        outputStream.write(newJsonText.getBytes('UTF-8'))
    } as OutputStreamCallback)

    // Set MIME type for downstream processing
    flowFile = session.putAttribute(flowFile, 'mime.type', 'application/json')

    session.transfer(flowFile, REL_SUCCESS)

} catch (Exception e) {
    log.error('Error preparing JSON for DB insert: ' + e.message, e)
    session.transfer(flowFile, REL_FAILURE)
}

