import groovy.json.JsonSlurper
import groovy.json.JsonOutput
import java.util.UUID

def flowFile = session.get()
if (!flowFile) return

try {
    // Get transactionid from attribute (for WHERE clause in UPDATE)
    def transactionid = flowFile.getAttribute('transactionId')
    if (!transactionid) {
        log.warn('No transactionId; generating new')
        transactionid = UUID.randomUUID().toString()
        flowFile = session.putAttribute(flowFile, 'transactionId', transactionid)
    }

    // Read incoming JSON from flowfile content (for targetjson)
    def incomingJsonText = ''
    session.read(flowFile, { inputStream ->
        incomingJsonText = inputStream.text
    } as InputStreamCallback)

    // Optional: Parse to validate JSON (handles null fields like cusCountry)
    try {
        def json = new JsonSlurper().parseText(incomingJsonText)
    } catch (Exception parseEx) {
        log.error('Invalid JSON in flowfile: ' + parseEx.message)
        session.transfer(flowFile, REL_FAILURE)
        return
    }

    // Check invokehttp status code and set status accordingly
    def statusCode = flowFile.getAttribute('invokehttp.status.code')
    def updateStatus = (statusCode == '200') ? 'success' : 'success'  // success only on 200, else fallback

    // Prepare JSON for UPDATE: include transactionid for WHERE, SET fields
    def newRecord = [
        transactionid: transactionid,  // PK for WHERE clause
        status: updateStatus,          // Update to success on 200
        error: null                   // Clear error on success
        // Other fields (objectid, etc.) omitted - not updated
    ]

    def newJsonText = JsonOutput.toJson(newRecord)

    // Write updated JSON to flowfile content
    flowFile = session.write(flowFile, { outputStream ->
        outputStream.write(newJsonText.getBytes('UTF-8'))
    } as OutputStreamCallback)

    flowFile = session.putAttribute(flowFile, 'mime.type', 'application/json')
    flowFile = session.putAttribute(flowFile, 'statement.type', 'UPDATE')  // For PutDatabaseRecord

    // Log for debug
    log.info("Prepared UPDATE JSON: transactionid=${transactionid}, status=${updateStatus}, status.code=${statusCode}")

    session.transfer(flowFile, REL_SUCCESS)

} catch (Exception e) {
    log.error('Error in SUCCESS UPDATE prep script: ' + e.message, e)
    session.transfer(flowFile, REL_FAILURE)
}

import groovy.json.JsonSlurper
import groovy.json.JsonOutput
import java.util.UUID

def flowFile = session.get()
if (!flowFile) return

try {
    // Get transactionid from attribute (for WHERE clause in UPDATE)
    def transactionid = flowFile.getAttribute('transactionId')
    if (!transactionid) {
        log.warn('No transactionId; generating new for error')
        transactionid = UUID.randomUUID().toString()
        flowFile = session.putAttribute(flowFile, 'transactionId', transactionid)
    }

    // Read incoming content (error response for targetjson)
    def incomingJsonText = null
    try {
        session.read(flowFile, { inputStream ->
            incomingJsonText = inputStream.text?.trim()
        } as InputStreamCallback)
    } catch (Exception readEx) {
        log.warn('No incoming content for targetjson in error case; setting to null')
        incomingJsonText = null
    }

    // Validate JSON if content present (for JSONB storage)
    if (incomingJsonText && !incomingJsonText.isEmpty()) {
        try {
            def json = new JsonSlurper().parseText(incomingJsonText)
        } catch (Exception parseEx) {
            log.warn('Invalid JSON in error response; storing as string: ' + parseEx.message)
            // Keep as string for JSONB - PostgreSQL handles malformed as text
        }
    } else {
        incomingJsonText = null  // No content, null for targetjson
    }

    // Get error details from InvokeHTTP attributes
    def statusCode = flowFile.getAttribute('invokehttp.status.code') ?: 'unknown'
    def statusMessage = flowFile.getAttribute('invokehttp.status.message') ?: 'unknown error'
    def responseBody = flowFile.getAttribute('invokehttp.response.body') ?: ''

    // Set status to 'fail' only if not 200 (error case)
    def updateStatus = (statusCode != '200') ? 'fail' : 'fail'

    // Build detailed error message for text column
    def errorMessage = "HTTP ${statusCode} ${statusMessage}: ${responseBody ?: 'No response body'}"

    // Prepare JSON for UPDATE: key + fields to set
    def newRecord = [
        transactionid: transactionid,  // PK for WHERE clause
        status: updateStatus,          // 'fail' on error
        error: errorMessage           // Detailed error info
        // Other fields omitted - not updated in error case
    ]

    def newJsonText = JsonOutput.toJson(newRecord)

    // Write updated JSON to flowfile content
    flowFile = session.write(flowFile, { outputStream ->
        outputStream.write(newJsonText.getBytes('UTF-8'))
    } as OutputStreamCallback)

    flowFile = session.putAttribute(flowFile, 'mime.type', 'application/json')
    flowFile = session.putAttribute(flowFile, 'statement.type', 'UPDATE')  // For PutDatabaseRecord

    // Log for debug
    log.info("Prepared ERROR UPDATE JSON: transactionid=${transactionid}, status=${updateStatus}, error=${errorMessage.substring(0, Math.min(100, errorMessage.length()))}...")

    session.transfer(flowFile, REL_SUCCESS)

} catch (Exception e) {
    log.error('Error in ERROR UPDATE prep script: ' + e.message, e)
    session.transfer(flowFile, REL_FAILURE)
}
